document.addEventListener('DOMContentLoaded',function(){
const config={
scrollStepDuration:"1",
bezier:".35,.73,.5,1",
buttonImage:"../smooscroll-logo.svg",
renderStyle:"normal",
buttonWidth:"90px",
buttonHeight:"90px",
positionRight:"20px",
positionBottom:"20px",
showAtPosition:80,
};
(function injectStyles(){const styleTag=document.createElement('style');const cssContent=`.scroll-container{position:fixed;top:0;width:100vw;height:100vh;overflow:hidden;}.smooth-content{position:relative;width:100%;transition:${config.scrollStepDuration}s cubic-bezier(${config.bezier});}#gotop{position:fixed;display:none;right:${config.positionRight};bottom:${config.positionBottom};z-index:11111;width:${config.buttonWidth};height:${config.buttonHeight};background-image:url("${config.buttonImage}");background-size:contain;image-rendering:${config.renderStyle};}#gotop:hover{cursor:pointer;}`;if(styleTag.styleSheet){styleTag.styleSheet.cssText = cssContent;}else{styleTag.appendChild(document.createTextNode(cssContent));}document.head.appendChild(styleTag);})();document.body.appendChild(document.createElement('div')).id ='gotop';let viewbox=document.getElementsByClassName("scroll-container")[0];if(!viewbox){var newElement2=document.createElement('div');newElement2.className='scroll-container';newElement2.id='scroll-container';document.body.appendChild(newElement2);viewbox=document.getElementsByClassName("scroll-container")[0];}let scrollbox=document.getElementsByClassName("smooth-content")[0];if(scrollbox){viewbox.appendChild(scrollbox);}else if(!scrollbox){alert("没有检测到smooth-content！\n页面效果无法生效！\n请参考官方文档在HTML中添加smooth-conten包裹层\n或者更换为自动部署版本的SmooScroll！");}const bodyChildren=Array.from(document.body.children);const elementsToMove=bodyChildren.filter(child =>{return child!==viewbox&&child==scrollbox&&child!==gotop;}); elementsToMove.forEach(element=>{scrollbox.appendChild(element);});function resize_body(){let height=scrollbox.offsetHeight;document.body.style.height=`${height}px`;};function scroll(){scrollbox.style.transform=`translateY(${-scrollY}px)`;};const resizeObserver=new ResizeObserver(()=>{resize_body();});resizeObserver.observe(scrollbox);window.addEventListener("scroll",scroll);window.addEventListener("load",resize_body);window.addEventListener("resize",resize_body);window.addEventListener("scroll",handleScroll);function handleScroll(){var oTop=document.body.scrollTop||document.documentElement.scrollTop;if(oTop>=config.showAtPosition){gotop.style.display="block";}else{gotop.style.display="none";}}gotop.onclick=function(){window.scrollTo({top:0,behavior:"smooth"});};});